CREATE DATABASE ProyectoIS2
USE DATABASE ProyectoIS2

CREATE TABLE `Persona` (
  `RUT` varchar(10) NOT NULL,
  `Nombres` varchar(50) NOT NULL,
  `Apellidos` varchar(50) NOT NULL,
  `Correo` varchar(100) NOT NULL,
  `Telefono` varchar(15),
  `Direccion` varchar(250),
  PRIMARY KEY (`RUT`)
);




/*
